CREATE TABLE IF NOT EXISTS pacer_response_codes (
  http_status_code INTEGER PRIMARY KEY,
  reason_phrase TEXT NOT NULL,
  enum_name TEXT NOT NULL,
  application_usage TEXT NOT NULL,
  description TEXT NOT NULL
);

CREATE TABLE IF NOT EXISTS pacer_case_types (
  case_type_code TEXT PRIMARY KEY
);

CREATE TABLE IF NOT EXISTS pacer_courts (
  court_id TEXT PRIMARY KEY,
  court_name TEXT NOT NULL
);

CREATE TABLE IF NOT EXISTS pacer_sortable_case_fields (
  field_name TEXT PRIMARY KEY
);

CREATE TABLE IF NOT EXISTS pacer_sortable_party_fields (
  field_name TEXT PRIMARY KEY
);

CREATE TABLE IF NOT EXISTS search_regions (
  region_code TEXT PRIMARY KEY,
  region_name TEXT NOT NULL
);






